  //   url: 'http://127.0.0.1:3000',
  //   reuseExistingServer: !process.env.CI,
  // },
});

class AddProductsPage {
    constructor(page) {
        this.page = page;
        // Selectors for login fields
        this.usernameField = '#user-name';
        this.passwordField = '#password';
        this.loginButton = '#login-button';
        
        // Selector for verifying successful login
        this.productList = '.inventory_list';
        
        // Selectors for adding products to the cart
        this.addToCartButtons = '.btn_inventory';
        this.cartIcon = '.shopping_cart_link';
        this.cartBadge = '.shopping_cart_badge';
    }

    // Navigates to a given URL
    async navigateTo(url) {
        await this.page.goto(url);
    }

    // Performs login using provided username and password
    async login(username, password) {
        await this.page.fill(this.usernameField, username);
        await this.page.fill(this.passwordField, password);
        await this.page.click(this.loginButton);
        // Wait until the product list is visible to confirm login success
        await this.page.waitForSelector(this.productList);
    }

    // Adds a product to the cart based on the index in the product list
    async addProductToCart(productIndex = 0) {
        const buttons = await this.page.$$(this.addToCartButtons);
        if (buttons.length > productIndex) {
            await buttons[productIndex].click();
        }
    }

    // Navigates to the cart page
    async goToCart() {
        await this.page.click(this.cartIcon);
    }

    // Returns the number of items in the cart as a string
    async getCartItemCount() {
        const cartBadge = await this.page.locator(this.cartBadge);
        return cartBadge.isVisible() ? await cartBadge.innerText() : '0';
    }
}

export default AddProductsPage;

const { createLogger, format, transports } = require('winston');

const logger = createLogger({
  level: 'info',
  format: format.combine(
    format.timestamp(),
    format.printf(({ timestamp, level, message }) => `${timestamp} [${level.toUpperCase()}]: ${message}`)
  ),
  transports: [
    new transports.Console(),
    new transports.File({ filename: 'logs/automation.log' })
  ],
});

module.exports = logger;

class CatalogPage {
    constructor(page) {
        this.page = page; // Store the page instance
        this.inventoryItems = '.inventory_item'; // Selector for all product items
        this.firstProduct = '.inventory_item:nth-child(1) .inventory_item_name'; // Selector for the first product name
        this.productDetailsContainer = '.inventory_details_container'; // Selector for product details container
        this.addToCartButtons = '.btn_inventory'; // Selector for add-to-cart buttons
        this.cartIcon = '.shopping_cart_link'; // Selector for the shopping cart icon
        this.backToProducts = '#back-to-products'; // Selector for the back button to return to the product list
    }

    async getProductsCount() {
        // Returns the total count of products available in the catalog
        return await this.page.locator(this.inventoryItems).count();
    }

    async viewFirstProduct() {
        // Clicks on the first product to view its details
        await this.page.click(this.firstProduct);
        // Waits until the product details container is visible
        await this.page.waitForSelector(this.productDetailsContainer);
    }

    async goBackToProducts() {
        // Clicks the back button to return to the product list
        await this.page.click(this.backToProducts);
        // Waits until the product list is visible again
        await this.page.waitForSelector(this.inventoryItems);
    }

    async addFirstProductToCart() {
        // Selects the first 'Add to Cart' button and clicks it
        const firstButton = await this.page.locator(this.addToCartButtons).first();
        await firstButton.click();
    }

    async goToCart() {
        // Clicks the cart icon to navigate to the cart page
        await this.page.click(this.cartIcon);
    }
}

export default CatalogPage; // Export the CatalogPage class for use in tests

const { expect } = require('@playwright/test'); // Import Playwright's expect function for assertions

class CheckoutPage {
    constructor(page) {
        this.page = page; // Store the Playwright page object
        
        // Selectors for checkout process
        this.checkoutButton = '[data-test="checkout"]'; // Button to proceed to checkout
        this.firstNameInput = '[data-test="firstName"]'; // Input field for first name
        this.lastNameInput = '[data-test="lastName"]'; // Input field for last name
        this.postalCodeInput = '[data-test="postalCode"]'; // Input field for postal code
        this.continueButton = '[data-test="continue"]'; // Button to continue to the next step
        this.finishButton = '[data-test="finish"]'; // Button to complete the purchase
        this.checkoutSummary = '.checkout_info'; // Section displaying checkout summary
        this.completeHeader = '.complete-header'; // Header confirming order completion
    }

    // Clicks the checkout button to navigate to the checkout page
    async goToCheckout() {
        await this.page.click(this.checkoutButton);
    }

    // Fills out the checkout form with the provided details and proceeds
    async fillCheckoutForm(firstName, lastName, postalCode) {
        await this.page.fill(this.firstNameInput, firstName);
        await this.page.fill(this.lastNameInput, lastName);
        await this.page.fill(this.postalCodeInput, postalCode);
        await this.page.click(this.continueButton);
    }

    // Completes the purchase by clicking the finish button
    async completePurchase() {
        await this.page.click(this.finishButton);
    }

    // Verifies that the checkout summary page is visible
    async verifyCheckoutPageIsVisible() {
        const summaryElement = this.page.locator(this.checkoutSummary);
        await summaryElement.waitFor({ state: 'visible', timeout: 10000 }); //Wait up to 10s for visibility
        await expect(summaryElement).toBeVisible();
    }
    
    // Verifies that the order completion message appears
    async verifyOrderCompletion() {
        await expect(this.page.locator(this.completeHeader)).toHaveText('Thank you for your order!');
    }
}

module.exports = CheckoutPage; // Export the CheckoutPage class for use in other files

class LoginPage {
    constructor(page) {
        this.page = page; // Store the Playwright page object for browser interactions
    }

    // Navigates to the specified URL
    async goto(url) {
        await this.page.goto(url);
    }

    // Performs login with the given username and password
    async login(username, password) {
        await this.page.fill('#user-name', username); // Fill in the username field
        await this.page.fill('#password', password); // Fill in the password field
        await this.page.click('#login-button'); // Click the login button to submit
    }

    // Retrieves the error message if login fails
    async getErrorMessage() {
        return await this.page.textContent('.error-message-container'); // Extract text content from the error message container
    }

    // Checks if the login was successful by verifying the visibility of the product inventory
    async isLoginSuccessful() {
        return await this.page.locator('.inventory_list').isVisible(); // Returns true if the product list is visible
    }
}

export default LoginPage; // Export the LoginPage class for use in other modules
